import { render } from 'lit-html';
import { createScopedHtml } from '../../../../index.js';
import { PageA } from 'page-a/index.js';
import { PageB } from 'page-b/index.js';

const html = createScopedHtml({
  'page-a': PageA,
  'page-b': PageB,
});

export class PageC extends HTMLElement {
  constructor() {
    super();
    this.attachShadow({ mode: 'open' });
  }

  connectedCallback() {
    render(html`
      <p>I am page C</p>
      <page-a></page-a>
      <page-b></page-b>
    `, this.shadowRoot);
  }
}
